import UIKit

final class MoviePresenter {

    // MARK: - Private properties -

    private unowned let view: MovieViewInterface
    private let interactor: MovieInteractorInterface
    private let wireframe: MovieWireframeInterface

    // MARK: - Lifecycle -

    init(view: MovieViewInterface, interactor: MovieInteractorInterface, wireframe: MovieWireframeInterface) {
        self.view = view
        self.interactor = interactor
        self.wireframe = wireframe
    }
}

// MARK: - Extensions -

extension MoviePresenter: MoviePresenterInterface {
    func loadMovies() {
        let source = interactor.fetchMovies()
        view.drawMoviesData(source: source)
    }
    
    func selectMovieItem(_ item: Movie.Item) {
        if let videoUrl = item.videoUrl?.toUrl() {
            wireframe.openVideo(url: videoUrl)
        } else {
            wireframe.showErrorMessage("Video no disponible")
        }
    }
}
